import React from "react";
import Reminder_input from "./Reminder_input";

const Reminderoption = (props) => {
  const handleClick = () => {
    props.onClick();
  };
  return (
    <div className="reminder-option" onClick={handleClick}>
      <span>{props.title}</span>
      <i class={props.icon}></i>
      {isDisplay && <Reminder_input />}
    </div>

  );
};

export default Reminderoption;
